      ACCEPT_TOKEN(sym_rethrow_builtin);
      END_STATE();
    case 251:
      ACCEPT_TOKEN(anon_sym_typedef);
      END_STATE();
    case 252:
      ACCEPT_TOKEN(anon_sym_Function);
      END_STATE();
    case 253:
      ACCEPT_TOKEN(anon_sym_abstract);
      END_STATE();
    case 254:
      ACCEPT_TOKEN(anon_sym_continue);
      END_STATE();
    case 255:
      if (lookahead == 't') ADVANCE(263);
      END_STATE();
    case 256:
      ACCEPT_TOKEN(anon_sym_deferred);
      END_STATE();
    case 257:
      if (lookahead == 'n') ADVANCE(264);
      END_STATE();
    case 258:
      ACCEPT_TOKEN(anon_sym_external);
      END_STATE();
    case 259:
      if (lookahead == 't') ADVANCE(265);
      END_STATE();
    case 260:
      if (lookahead == 'e') ADVANCE(266);
      END_STATE();
    case 261:
      ACCEPT_TOKEN(anon_sym_operator);
      END_STATE();
    case 262:
      ACCEPT_TOKEN(anon_sym_required);
      END_STATE();
    case 263:
      ACCEPT_TOKEN(anon_sym_covariant);
      END_STATE();
    case 264:
      ACCEPT_TOKEN(anon_sym_extension);
      END_STATE();
    case 265:
      if (lookahead == 's') ADVANCE(267);
      END_STATE();
    case 266:
      ACCEPT_TOKEN(anon_sym_interface);
      END_STATE();
    case 267:
      ACCEPT_TOKEN(anon_sym_implements);
      END_STATE();
    default:
      return false;
  }
}

static bool scan_multiline_comments(TSLexer *lexer) {

    bool documentation_comment = false;
    advance(lexer);
    if (lexer->lookahead != '*') return false;
    advance(lexer);
    if (lexer->lookahead == '*') documentation_comment = true;

    bool after_star = false;
    unsigned nesting_depth = 1;
    for (;;) {
      switch (lexer->lookahead) {
        case '\0':
          return false;
        case '*':
          advance(lexer);
          after_star = true;
          break;
        case '/':
          if (after_star) {
            advance(lexer);
            after_star = false;
            nesting_depth--;
            if (nesting_depth == 0) {
              if (!documentation_comment) {
                lexer->result_symbol = BLOCK_COMMENT;
              } else {
                lexer->result_symbol = DOCUMENTATION_BLOCK_COMMENT;
              }
              return true;
            }
          } else {
            advance(lexer);
            after_star = false;
            if (lexer->lookahead == '*') {
              nesting_depth++;
              advance(lexer);
            }
          }
          break;
        default:
          advance(lexer);
          after_star = false;
          break;
      }
    }
  return false;
}

static bool scan_templates(TSLexer *lexer, const bool *valid_symbols) {
  if(valid_symbols[TEMPLATE_CHARS_DOUBLE]) {
              lexer->result_symbol = TEMPLATE_CHARS_DOUBLE;
  } else if (valid_symbols[TEMPLATE_CHARS_SINGLE]) {
              lexer->result_symbol = TEMPLATE_CHARS_SINGLE;
  } else if (valid_symbols[TEMPLATE_CHARS_SINGLE_SINGLE]) {
              lexer->result_symbol = TEMPLATE_CHARS_SINGLE_SINGLE;
  } else {
              lexer->result_symbol = TEMPLATE_CHARS_DOUBLE_SINGLE;
  }
  for (bool has_content = false;; has_content = true) {
    lexer->mark_end(lexer);
    switch (lexer->lookahead) {
      case '\'':
      case '"':
        return has_content;
      case '\n':
        if (valid_symbols[TEMPLATE_CHARS_DOUBLE_SINGLE] || valid_symbols[TEMPLATE_CHARS_SINGLE_SINGLE]) return false;
        advance(lexer);
        break;
      case '\0':
        return false;
      case '$':
        return has_content;
      case '\\':
        if (valid_symbols[TEMPLATE_CHARS_RAW_SLASH]) {
            lexer->result_symbol = TEMPLATE_CHARS_RAW_SLASH;
            advance(lexer);
        } else {
            return has_content;
        }
        break;
      default:
        advance(lexer);
    }
  }
  return true;
}

bool tree_sitter_dart_external_scanner_scan(void *payload, TSLexer *lexer,
                                                  const bool *valid_symbols) {
  if (
      valid_symbols[TEMPLATE_CHARS_DOUBLE] ||
      valid_symbols[TEMPLATE_CHARS_SINGLE] ||
      valid_symbols[TEMPLATE_CHARS_DOUBLE_SINGLE] ||
      valid_symbols[TEMPLATE_CHARS_SINGLE_SINGLE]
  ) {
    return scan_templates(lexer, valid_symbols);
  }
  while (iswspace(lexer->lookahead)) lexer->advance(lexer, true);

  if (lexer->lookahead == '/') {
    return scan_multiline_comments(lexer);
  }
  return false;
}

function sep1(rule, separator) {
    return seq(rule, repeat(seq(separator, rule)));
}

function sep2(rule, separator) {
    return seq(rule, repeat1(seq(separator, rule)));
}

function commaSep1(rule) {
    return seq(rule, repeat(seq(',', rule)))
}

function commaSep2TrailingComma(rule) {
    return seq(rule, repeat1(seq(',', rule)), optional(','))
}

function commaSep1TrailingComma(rule) {
    return seq(rule, repeat(seq(',', rule)), optional(','))
}

function pureBinaryRun(rule, separator, precedence){
   return prec.left(
        precedence,
        choice(
            sep2(
                rule,
                separator
            )))
}

function binaryRunLeft(rule, separator, superItem, precedence) {
    return prec.left( //left
        precedence,
        choice(
            sep2(
                // $.bitwise_xor_expression,
                rule,
                separator
            ),
            seq(
                superItem,
                repeat1(
                    seq(
                        separator,
                        rule,
                        // $.bitwise_xor_expression
                    )
                )
            )
        )
    )
}

