def extract_imports(content: str) -> List[str]:
    """
    Extract imported module names from Python code using AST.
    Returns list of module names (e.g., 'os', 'ndoc.atoms').
    Handles 'import x', 'from x import y'.
    """
    imports = set()
    try:
        tree = ast.parse(content)
        for node in ast.walk(tree):
            if isinstance(node, ast.Import):
                for alias in node.names:
                    imports.add(alias.name)
            elif isinstance(node, ast.ImportFrom):
                if node.module:
                    # For "from x import y", only add "x" as the primary dependency
                    # Adding "x.y" often leads to redundancy (e.g. datetime.datetime)
                    imports.add(node.module)
    except SyntaxError:
        # Fallback to regex if AST fails (e.g. invalid syntax in some files)
        # import x, y
        for match in re.finditer(r"^\s*import\s+([\w.,\s]+)", content, re.MULTILINE):
            for part in match.group(1).split(','):
                imports.add(part.strip())
        # from x import y
        for match in re.finditer(r"^\s*from\s+([\w.]+)\s+import\s+", content, re.MULTILINE):
            imports.add(match.group(1))
    except Exception:
        pass
    return list(imports)

def parse_requirements_txt(file_path: Path) -> List[str]:
    """Parse requirements.txt"""
    deps = []
    if not file_path.exists():
        return deps

    try:
        content = file_path.read_text(encoding='utf-8')
        for line in content.splitlines():
            line = line.strip()
            if not line or line.startswith('#'):
                continue
            # Simple cleanup of version specifiers to get package name
            # e.g., "pandas>=1.0.0" -> "pandas"
            # Handle "pkg @ url" or "git+https..."
            if line.startswith(('git+', 'http', 'svn+')):
                deps.append(line)
                continue

            match = re.match(r'^([a-zA-Z0-9_\-\.]+)', line)
            if match:
                deps.append(line) # Keep full spec for info
    except Exception:
        pass
    return deps

def parse_pyproject_toml(file_path: Path) -> List[str]:
    """Parse pyproject.toml (Poetry/Flit/Setuptools)"""
    deps = []
    if not file_path.exists():
        return deps

    try:
        # Simple TOML parsing without external lib for now (std lib tomllib in 3.11+)
        # Fallback to regex for older python if needed, or just reading text
        # Assuming [tool.poetry.dependencies] or [project.dependencies]
        content = file_path.read_text(encoding='utf-8')

        # Very rough extraction for robustness without toml lib
        in_dep_section = False
        for line in content.splitlines():
            line = line.strip()
            if line.startswith('[') and line.endswith(']'):
                section = line[1:-1]
                if section in ['tool.poetry.dependencies', 'project.dependencies']:
                    in_dep_section = True
                else:
                    in_dep_section = False
                continue

            if in_dep_section and '=' in line:
                key = line.split('=')[0].strip()
                if key and key != 'python':
                    deps.append(key)
    except Exception:
        pass
    return deps

def parse_package_json(file_path: Path) -> List[str]:
    """Parse package.json"""
    deps = []
    if not file_path.exists():
        return deps

    try:
        data = json.loads(file_path.read_text(encoding='utf-8'))
        dependencies = data.get('dependencies', {})
        dev_dependencies = data.get('devDependencies', {})

        # Combine
        for name, ver in dependencies.items():
            deps.append(f"{name} ({ver})")
        for name, ver in dev_dependencies.items():
            deps.append(f"{name} (dev: {ver})")

    except Exception:
        pass
    return deps

def extract_cpp_includes(content: str) -> List[str]:
    """
    Extract #include directives from C++ code.
    Returns list of included files/libs.
    """
    includes = set()
    try:
        # Match #include <vector> or #include "my_header.h"
        # Allow spaces between # and include
        matches = re.findall(r'#\s*include\s*[<"]([^>"]+)[>"]', content)
        for inc in matches:
            includes.add(inc)
    except Exception:
        pass
    return sorted(list(includes))

def extract_csharp_usings(content: str) -> List[str]:
    """
    Extract usings from C# code.
    Matches: using System; using Foo.Bar;
    """
    usings = set()
    # Matches: using Namespace;
    pattern = re.compile(r"^\s*using\s+([\w\.]+)\s*;", re.MULTILINE)

    for match in pattern.finditer(content):
        usings.add(match.group(1))

    return list(usings)

def parse_csproj(file_path: Path) -> List[str]:
    """
    Parse .csproj files for PackageReference.
    """
    deps = set()
    try:
        content = file_path.read_text(encoding='utf-8', errors='ignore')
        # Matches: <PackageReference Include="PackageName" ... />
        pattern = re.compile(r'<PackageReference\s+Include="([^"]+)"', re.MULTILINE)
        for match in pattern.finditer(content):
            deps.add(match.group(1))
    except Exception:
        pass
    return list(deps)

def extract_dependencies(content: str, file_path: Path) -> List[str]:
    """
    Generic dependency extractor based on file extension.
    Dispatch to specific parser based on file suffix.
    """
    ext = file_path.suffix.lower()
    if ext in SOURCE_PARSERS:
        try:
            return SOURCE_PARSERS[ext](content)
        except Exception:
            return []
    return []

def get_project_dependencies(root_path: Path, ignore_patterns: Set[str] = None) -> Dict[str, List[str]]:
    """
    Detect and parse dependency files recursively.
    Uses fs.walk_files for efficient pruning.
    Returns: Dict[RelativeFilePath, List[Dependency]]
    """
    results = {}

    # Manifest files
    manifest_parsers = {
        'requirements.txt': parse_requirements_txt,
        'pyproject.toml': parse_pyproject_toml,
        'package.json': parse_package_json,
        'pubspec.yaml': parse_pubspec_yaml,
        'CMakeLists.txt': parse_cmake_lists,
    }

    ignores = list(ignore_patterns or DEFAULT_IGNORE_PATTERNS)

    # Walk with pruning
    for path in fs.walk_files(root_path, ignore_patterns=ignores):
        # 1. Check Manifests (Exact Name or Extension)
        if path.name in manifest_parsers:
            parser = manifest_parsers[path.name]
            try:
                deps = parser(path)
                if deps:
                    rel_path = fs.get_relative_path(path, root_path)
                    results[rel_path] = deps
            except Exception:
                pass
        elif path.suffix.lower() == '.csproj':
             try:
                deps = parse_csproj(path)
                if deps:
                    rel_path = fs.get_relative_path(path, root_path)
                    results[rel_path] = deps
             except Exception:
                pass

        # 2. Check Source Code (Extension)
        if path.suffix.lower() in SOURCE_PARSERS:
            parser = SOURCE_PARSERS[path.suffix.lower()]
            try:
                content = path.read_text(encoding='utf-8', errors='ignore')
                deps = parser(content)
                if deps:
                    rel_path = fs.get_relative_path(path, root_path)
                    # Merge if both exist (rare but possible if a file matches both)
                    if rel_path in results:
                        results[rel_path].extend(deps)
                    else:
                        results[rel_path] = deps
            except Exception:
                pass

    return results

class FileFilter:
    """
    文件过滤器配置 (File Filter Configuration).
    """
    ignore_patterns: Set[str] = field(default_factory=set)
    allow_extensions: Set[str] = field(default_factory=set)
    spec: Optional[pathspec.PathSpec] = None

    @property
    def has_extension_filter(self) -> bool:
        return bool(self.allow_extensions)

def load_gitignore(root: Path) -> Optional[pathspec.PathSpec]:
    """Load .gitignore from root directory."""
    gitignore_path = root / ".gitignore"
    if gitignore_path.exists():
        try:
            with open(gitignore_path, 'r', encoding='utf-8') as f:
                return pathspec.PathSpec.from_lines('gitwildmatch', f)
        except Exception:
            pass
    return None

def should_ignore(path: Path, filter_config: FileFilter, root: Path = None) -> bool:
    """
    检查是否应该忽略 (Check if should ignore).
    Implementation: Table lookup (Set) + Gitignore (PathSpec).
    """
    name = path.name

    # 1. Hidden file check (Rule)
    if name.startswith('.') and name != '.':
        return True

    # 2. Ignore list lookup (Data)
    if name in filter_config.ignore_patterns:
        return True

    # 3. .gitignore check (Logic as Data)
    if filter_config.spec and root:
        try:
            rel_path = path.relative_to(root).as_posix()
            if filter_config.spec.match_file(rel_path):
                return True
        except ValueError:
            pass

    return False

def list_dir(path: Path, filter_config: FileFilter, root: Path = None) -> List[Path]:
    """
    列出目录内容并应用过滤 (List directory contents with filtering).
    Returns sorted list: Directories first, then files.
    """
    if not path.is_dir():
        return []

    # Auto-load gitignore if not provided and we are at root
    if not filter_config.spec and root and path == root:
        filter_config.spec = load_gitignore(root)

    try:
        entries = path.iterdir()
    except PermissionError:
        return []

    filtered_entries = []
    for entry in entries:
        # Check ignore patterns
        if should_ignore(entry, filter_config, root):
            continue

        # Check extensions (files only)
        if entry.is_file() and filter_config.has_extension_filter:
            if entry.suffix.lower() not in filter_config.allow_extensions:
                continue

        filtered_entries.append(entry)

    # Sort: Directories first (is_file=False), then files (is_file=True), then by name
    return sorted(filtered_entries, key=lambda x: (x.is_file(), x.name))

def get_relative_path(path: Path, root: Path) -> str:
    """
    获取相对路径字符串 (Get relative path string).

    Args:
        path: 目标路径
        root: 根路径

    Returns:
        str: 相对路径字符串
    """
    try:
        rel = path.relative_to(root)
        return rel.as_posix()
    except ValueError:
        return path.as_posix()

def read_text(path: Path) -> Optional[str]:
    """
    安全读取文件内容 (Safely read file content).

    Args:
        path: 文件路径

    Returns:
        Optional[str]: 文件内容
    """
    if not path.exists():
        return None

    def _read():
        return path.read_text(encoding='utf-8', errors='ignore')

    return safe_io(_read, f"Error reading {path}: {{e}}")

def read_head(path: Path, n_bytes: int = 2048) -> Optional[str]:
    """
    读取文件头部内容 (Read file head content).
    Useful for quick scanning without loading huge files.
    """
    if not path.exists():
        return None

    def _read():
        with open(path, 'rb') as f:
            chunk = f.read(n_bytes)
            return chunk.decode('utf-8', errors='ignore')

    return safe_io(_read, f"Error reading head of {path}: {{e}}")

def _read():
        with open(path, 'rb') as f:
            chunk = f.read(n_bytes)
            return chunk.decode('utf-8', errors='ignore')

def _delete():
        if path.exists():
            path.unlink()
        return True

def call_llm(prompt: str, system_prompt: str = "You are a helpful assistant.") -> Optional[str]:
    """
    Generic LLM call via environment variables.
    Supports: OPENAI_API_KEY, GEMINI_API_KEY (via Google AI API), DEEPSEEK_API_KEY.
    """
    # 1. Try DeepSeek (Commonly used in China)
    if os.getenv("DEEPSEEK_API_KEY"):
        return _call_openai_compatible(
            api_key=os.getenv("DEEPSEEK_API_KEY"),
            base_url="https://api.deepseek.com/v1",
            model="deepseek-chat",
            prompt=prompt,
            system_prompt=system_prompt
        )

    # 2. Try OpenAI
    if os.getenv("OPENAI_API_KEY"):
        return _call_openai_compatible(
            api_key=os.getenv("OPENAI_API_KEY"),
            base_url="https://api.openai.com/v1",
            model=os.getenv("OPENAI_MODEL", "gpt-4o"),
            prompt=prompt,
            system_prompt=system_prompt
        )

    # 3. Try Gemini (Google AI Studio)
    if os.getenv("GEMINI_API_KEY"):
        return _call_gemini(
            api_key=os.getenv("GEMINI_API_KEY"),
            prompt=prompt,
            system_prompt=system_prompt
        )

    print("❌ Error: No LLM API key found (DEEPSEEK_API_KEY, OPENAI_API_KEY, or GEMINI_API_KEY).")
    return None

def _call_openai_compatible(api_key: str, base_url: str, model: str, prompt: str, system_prompt: str) -> Optional[str]:
    url = f"{base_url}/chat/completions"
    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {api_key}"
    }
    data = {
        "model": model,
        "messages": [
            {"role": "system", "content": system_prompt},
            {"role": "user", "content": prompt}
        ],
        "temperature": 0.7
    }

    try:
        req = urllib.request.Request(url, data=json.dumps(data).encode(), headers=headers)
        with urllib.request.urlopen(req) as response:
            res_data = json.loads(response.read().decode())
            return res_data["choices"][0]["message"]["content"]
    except Exception as e:
        print(f"❌ LLM Call Failed: {e}")
        return None

def _call_gemini(api_key: str, prompt: str, system_prompt: str) -> Optional[str]:
    # Gemini API format is slightly different
    url = f"https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key={api_key}"
    headers = {"Content-Type": "application/json"}

    # Combine system prompt and user prompt for Gemini if needed, 
    # though 1.5 supports system_instruction.
    data = {
        "system_instruction": {"parts": {"text": system_prompt}},
        "contents": {"parts": {"text": prompt}}
    }

    try:
        req = urllib.request.Request(url, data=json.dumps(data).encode(), headers=headers)
        with urllib.request.urlopen(req) as response:
            res_data = json.loads(response.read().decode())
            return res_data["candidates"][0]["content"]["parts"][0]["text"]
    except Exception as e:
        print(f"❌ Gemini Call Failed: {e}")
        return None

def get_cache(root: Path) -> cache.FileCache:
    global _CACHE
    if _CACHE is None:
        cache_dir = root / ".ndoc" / "cache"
        _CACHE = cache.FileCache(cache_dir)
    return _CACHE

def extract_docstring(content: str) -> str:
    """
    提取文件顶部的 Docstring (Extract file-level docstring).
    Supports:
    - Python/Ruby/Shell (# comments)
    - Python/JS/Dart (Block comments or Triple quotes)
    - C++/Dart/Rust (/// or // comments)
    """
    subset = content[:2000].strip()  # Optimization: only check header
    if not subset:
        return ""

    # 1. Check for Triple Quotes (Python/JS/Dart)
    for pattern in DOCSTRING_PATTERNS:
        match = pattern.search(subset)
        if match:
            return clean_docstring(match.group(0))

    # 2. Check for Block Comments (/** ... */ or /* ... */)
    if subset.startswith('/*'):
        end_idx = subset.find('*/')
        if end_idx != -1:
            return clean_docstring(subset[:end_idx+2])

    # 3. Check for Line Comments (///, //, or #)
    lines = subset.split('\n')
    doc_lines = []

    # Identify the comment style of the first line
    first_line = lines[0].strip()
    comment_prefix = None
    if first_line.startswith('///'):
        comment_prefix = '///'
    elif first_line.startswith('//'):
        comment_prefix = '//'
    elif first_line.startswith('#'):
        # Skip shebang
        if first_line.startswith('#!'):
            if len(lines) > 1:
                lines = lines[1:]
                first_line = lines[0].strip()
                if first_line.startswith('#'):
                    comment_prefix = '#'
        else:
            comment_prefix = '#'

    if comment_prefix:
        for line in lines:
            line = line.strip()
            if line.startswith(comment_prefix):
                doc_lines.append(line[len(comment_prefix):].strip())
            elif not line:
                continue
            else:
                break
        return "\n".join(doc_lines).strip()

    return ""

def parse_tags(content: str) -> List[Tag]:
    """
    从内容中提取标签 (Extract tags from content).
    Uses utility function for consistency.
    """
    return extract_tags_from_text(content)

def extract_summary(content: str, docstring: str) -> str:
    """
    提取文件摘要 (Extract summary).
    Priority:
    1. @SUMMARY tag
    2. Docstring first line
    3. Empty
    """
    # 1. @SUMMARY tag
    # Use simple regex for this specific tag to avoid full parse overhead if called independently
    match = re.search(
        r"^\s*(?:#+|//|<!--|>)?\s*@SUMMARY\s+(.*?)$", content, re.MULTILINE
    )
    if match:
        return match.group(1).strip()

    # 2. Docstring first line
    if docstring:
        lines = docstring.strip().split("\n")
        if lines:
            return lines[0].strip()

    return ""

def ensure_rules_file(root_path: Path, force: bool = False) -> bool:
    """
    确保 _RULES.md 存在 (Ensure _RULES.md exists).
    :param force: If True, overwrite existing file.
    """
    rules_file = root_path / "_RULES.md"
    if force or not rules_file.exists():
        if force:
             print(f"Restoring default Rules Configuration at {rules_file.name}...")
        else:
             print(f"Creating default Rules Configuration at {rules_file.name}...")
        return io.write_text(rules_file, RULES_TEMPLATE)
    return False

def format_dependencies(ctx: FileContext) -> str:
    """
    Format dependencies list (if any).
    Uses indentation and keyword markers for visibility.
    """
    try:
        content = io.read_text(ctx.path)
        imports = deps.extract_dependencies(content, ctx.path)
        if not imports:
            return ""

        # Use simple indent line
        # Limit the number of dependencies shown to keep it concise
        MAX_DEPS = 8
        if len(imports) > MAX_DEPS:
            deps_str = ", ".join(list(imports)[:MAX_DEPS]) + " ..."
        else:
            deps_str = ", ".join(imports)

        return f" @DEP: {deps_str}"
    except:
        return ""

def collect_imports(root: Path) -> Dict[str, List[str]]:
    """
    Collect imports from all Python files.
    Returns: Dict[RelativePath, List[ImportedModule]]
    """
    import_map = {}

    # Only scan .py files
    # Ignore common artifacts
    ignore = {'.git', '__pycache__', 'venv', 'env', 'node_modules', 'dist', 'build', 'site-packages'}
    files = fs.walk_files(root, ignore_patterns=list(ignore), extensions={'.py'})

    for file_path in files:
        try:
            content = io.read_text(file_path)
            imports = deps.extract_imports(content)

            # Normalize path relative to root
            rel_path = file_path.relative_to(root).as_posix()
            import_map[rel_path] = imports
        except Exception as e:
            print(f"Error parsing {file_path}: {e}")

    return import_map

def generate_mermaid_graph(graph: Dict[str, Set[str]]) -> str:
    """Generate Mermaid flowchart"""
    lines = ["graph TD"]

    # Sort for stability
    for source in sorted(graph.keys()):
        targets = sorted(list(graph[source]))
        for target in targets:
            # Avoid self-loops
            if source == target:
                continue
            lines.append(f"    {source} --> {target}")

    if len(lines) == 1:
        return "*No internal dependencies detected.*"

    return "```mermaid\n" + "\n".join(lines) + "\n```"

def _check_import(module_name: str) -> bool:
    try:
        importlib.import_module(module_name)
        return True
    except ImportError:
        return False

