def get_service(root: Path) -> LSPService:
    global _INSTANCE
    if _INSTANCE is None:
        _INSTANCE = LSPService(root)
    return _INSTANCE

def get_language(lang_key: str) -> Optional[Language]:
    if lang_key in _LANGUAGES:
        return _LANGUAGES[lang_key]

    lang_obj = None
    if lang_key == 'python' and tspython:
        lang_obj = Language(tspython.language())
    elif lang_key == 'cpp' and tscpp:
        lang_obj = Language(tscpp.language())
    elif lang_key == 'javascript' and tsjs:
        lang_obj = Language(tsjs.language())
    elif lang_key == 'typescript' and tsts:
        lang_obj = Language(tsts.language_typescript())
    elif lang_key == 'go' and tsgo:
        lang_obj = Language(tsgo.language())
    elif lang_key == 'rust' and tsrust:
        lang_obj = Language(tsrust.language())
    elif lang_key == 'dart' and tsdart:
        lang_obj = Language(tsdart.language())
    elif lang_key == 'c_sharp' and tscsharp:
        lang_obj = Language(tscsharp.language())
    elif lang_key == 'java' and tsjava:
        lang_obj = Language(tsjava.language())

    if lang_obj:
        _LANGUAGES[lang_key] = lang_obj

    return lang_obj

class AstNode:
    """
    通用 AST 节点数据结构 (Generic AST Node Data).
    """
    type: str
    text: str
    start_point: tuple[int, int]  # (row, col)
    end_point: tuple[int, int]
    children: list['AstNode'] = field(default_factory=list)

    @property
    def start_line(self) -> int:
        return self.start_point[0] + 1

    @property
    def end_line(self) -> int:
        return self.end_point[0] + 1

def get_parser(lang_key: str = 'python') -> Optional[Parser]:
    lang = get_language(lang_key)
    if not lang:
        return None
    try:
        return Parser(lang)
    except Exception:
        return None

def parse_code(content: str, file_path: Optional[Path] = None) -> Optional[Tree]:
    if file_path:
        lang_key = get_lang_key(file_path)
    else:
        lang_key = 'python'

    if not lang_key:
        return None

    parser = get_parser(lang_key)
    if not parser:
        return None

    return parser.parse(bytes(content, "utf8"))

def get_lang_key(file_path: Path) -> Optional[str]:
    """
    根据文件后缀获取语言标识符 (Get language ID by extension).
    """
    ext = file_path.suffix.lower()
    return langs.get_lang_id_by_ext(ext)

def query_tree(tree: Tree, query_scm: str, lang_key: str = 'python') -> list[dict]:
    lang = get_language(lang_key)
    if not lang:
        return []

    query = lang.query(query_scm)
    captures = query.captures(tree.root_node)

    results = []
    if isinstance(captures, dict):
        for name, nodes in captures.items():
            for node in nodes:
                results.append({'name': name, 'node': node})
    else:
        for node, name in captures:
            results.append({'name': name, 'node': node})
    return results

def truncate(text: str, max_len: int = 100) -> str:
    if len(text) <= max_len:
        return text
    return text[:max_len] + "..."

def node_to_data(node: Node, include_children: bool = False) -> AstNode:
    data = AstNode(
        type=node.type,
        text=node.text.decode("utf8"),
        start_point=node.start_point,
        end_point=node.end_point
    )
    if include_children:
        data.children = [node_to_data(child, True) for child in node.children]
    return data

def _get_parent_name(node: Node, lang_key: str = 'python') -> Optional[str]:
    lang_def = langs.get_lang_def(lang_key)
    if not lang_def:
        return None

    target_types = lang_def.CLASS_TYPES
    if not target_types:
        return None

    curr = node.parent
    while curr:
        if curr.type in target_types:
            name_node = curr.child_by_field_name('name')
            if name_node:
                return name_node.text.decode('utf8')
        curr = curr.parent
    return None

def _is_inside_function(node: Node) -> bool:
    curr = node.parent
    while curr:
        if curr.type in ('function_definition', 'async_function_definition', 'method_definition'):
            return True
        curr = curr.parent
    return False

def _extract_docstring_from_node(node: Node, content_bytes: bytes, lang_key: str = 'python') -> Optional[str]:
    lang_def = langs.get_lang_def(lang_key)
    if not lang_def:
        return None
    return lang_def.extract_docstring(node, content_bytes)

def _is_async_function(node: Node, lang_key: str = 'python') -> bool:
    lang_def = langs.get_lang_def(lang_key)
    if not lang_def:
        return False
    try:
        text = node.text.decode('utf8').strip()
        return lang_def.is_async(text)
    except:
        return False

def find_calls(tree: Tree, lang_key: str = 'python') -> List[str]:
    lang = get_language(lang_key)
    if not lang:
        return []

    lang_def = langs.get_lang_def(lang_key)
    query_scm = getattr(lang_def, 'SCM_CALLS', None) or getattr(lang_def, 'CALL_QUERY', None)
    if not query_scm:
        return []

    results = query_tree(tree, query_scm, lang_key)

    calls = []
    for capture in results:
        if capture['name'] in ('call', 'call_name'):
            node = capture['node']
            calls.append(node.text.decode('utf8'))
    return list(set(calls))

def find_imports(tree: Tree, lang_key: str = 'python') -> List[str]:
    lang = get_language(lang_key)
    if not lang:
        return []

    lang_def = langs.get_lang_def(lang_key)
    query_scm = getattr(lang_def, 'SCM_IMPORTS', None)
    if not query_scm:
        return []

    results = query_tree(tree, query_scm, lang_key)

    imports = []
    for capture in results:
        if capture['name'] == 'import':
            node = capture['node']
            imports.append(node.text.decode('utf8').strip())
    return list(set(imports))

def extract_symbols(tree: Tree, content_bytes: bytes, file_path: Optional[Path] = None) -> List[Symbol]:
    lang_key = 'python'
    if file_path:
        ext = file_path.suffix.lower()
        lang_key = langs.get_lang_id_by_ext(ext) or 'python'

    lang_obj = get_language(lang_key)
    if not lang_obj:
        return []

    lang_def = langs.get_lang_def(lang_key)
    if not lang_def or not lang_def.SCM_QUERY:
        return []

    query = lang_obj.query(lang_def.SCM_QUERY)
    matches = query.matches(tree.root_node)

    unique_symbols = {}

    for match in matches:
        captures = match[1]

        node = None
        kind = "unknown"
        name = ""
        signature = ""
        decorators = []
        bases = []
        parent_name = None
        docstring = None
        visibility_text = ""

        # ... (rest of the logic remains same, but Symbol should include path)
        # Note: We'll update the loop at the end to include path if available

        # Pre-extract visibility/modifiers
        for capture_name, capture_node in captures.items():
            if capture_name == 'visibility':
                v_nodes = capture_node if isinstance(capture_node, list) else [capture_node]
                visibility_text = " ".join([v.text.decode('utf8').strip() for v in v_nodes])
                break

        # C# modifiers fallback
        if not visibility_text and lang_key == 'c_sharp':
            target_node = None
            if 'class_def' in captures: target_node = captures['class_def']
            elif 'func_def' in captures: target_node = captures['func_def']
            elif 'struct_def' in captures: target_node = captures['struct_def']
            if isinstance(target_node, list): target_node = target_node[0]
            if target_node:
                for child in target_node.children:
                    if child.type == 'modifier':
                        visibility_text += child.text.decode('utf8') + " "
                visibility_text = visibility_text.strip()

        if 'deco' in captures:
            deco_nodes = captures['deco']
            if not isinstance(deco_nodes, list): deco_nodes = [deco_nodes]
            for deco in deco_nodes:
                decorators.append(deco.text.decode('utf8').strip())

        if 'class_def' in captures:
            node = captures['class_def'][0] if isinstance(captures['class_def'], list) else captures['class_def']
            kind = 'class'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')

            if lang_key == 'python':
                bases_node = node.child_by_field_name('superclasses')
                if bases_node:
                    for base in bases_node.children:
                        if base.type in ('identifier', 'attribute'):
                            bases.append(base.text.decode('utf8'))
            elif lang_key == 'c_sharp':
                if 'bases' in captures:
                    bases_node = captures['bases'][0] if isinstance(captures['bases'], list) else captures['bases']
                    for child in bases_node.children:
                        if child.type == 'base_type':
                            bases.append(child.text.decode('utf8'))
            parent_name = _get_parent_name(node, lang_key)

        elif 'struct_def' in captures:
            node = captures['struct_def'][0] if isinstance(captures['struct_def'], list) else captures['struct_def']
            kind = 'struct'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')
            parent_name = _get_parent_name(node, lang_key)

        elif 'enum_def' in captures:
            node = captures['enum_def'][0] if isinstance(captures['enum_def'], list) else captures['enum_def']
            kind = 'enum'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')
            parent_name = _get_parent_name(node, lang_key)

        elif 'record_def' in captures:
            node = captures['record_def'][0] if isinstance(captures['record_def'], list) else captures['record_def']
            kind = 'record'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')
            parent_name = _get_parent_name(node, lang_key)

        elif 'func_def' in captures:
            node = captures['func_def'][0] if isinstance(captures['func_def'], list) else captures['func_def']
            kind = 'function'
            if _is_async_function(node, lang_key):
                kind = 'async_function'

            for deco_text in decorators:
                if '@property' in deco_text: kind = 'property'
                elif '@classmethod' in deco_text: kind = 'classmethod'
                elif '@staticmethod' in deco_text: kind = 'staticmethod'

            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')

            params_node = captures.get('params')[0] if isinstance(captures.get('params'), list) else captures.get('params')
            ret_node = captures.get('ret')[0] if isinstance(captures.get('ret'), list) else captures.get('ret')

            params_text = params_node.text.decode('utf8') if params_node else None
            ret_text = ret_node.text.decode('utf8') if ret_node else None
            signature = lang_def.format_signature(params_text, ret_text)
            parent_name = _get_parent_name(node, lang_key)

        elif 'field_def' in captures:
            node = captures['field_def'][0] if isinstance(captures['field_def'], list) else captures['field_def']
            if _is_inside_function(node):
                continue
            kind = 'variable'
            if 'field_name' in captures:
                name = captures['field_name'][0].text.decode('utf8')

            type_text = ""
            value_text = ""
            if 'field_type' in captures:
                type_node = captures['field_type'][0] if isinstance(captures['field_type'], list) else captures['field_type']
                type_text = type_node.text.decode('utf8').strip()
            if 'field_value' in captures:
                val_node = captures['field_value'][0] if isinstance(captures['field_value'], list) else captures['field_value']
                value_text = truncate(val_node.text.decode('utf8').strip(), MAX_VALUE_LENGTH)

            if type_text and value_text:
                signature = f"{type_text} = {value_text}" if type_text.startswith(':') else f": {type_text} = {value_text}"
            elif type_text:
                signature = type_text if type_text.startswith(':') else f": {type_text}"
            elif value_text:
                signature = f" = {value_text}"
            parent_name = _get_parent_name(node, lang_key)

        elif 'property_def' in captures:
            node = captures['property_def'][0] if isinstance(captures['property_def'], list) else captures['property_def']
            kind = 'property'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')
            ret_node = captures.get('ret')[0] if isinstance(captures.get('ret'), list) else captures.get('ret')
            ret_text = ret_node.text.decode('utf8') if ret_node else None
            signature = lang_def.format_signature(None, ret_text)
            parent_name = _get_parent_name(node, lang_key)

        elif 'namespace_def' in captures:
            node = captures['namespace_def'][0] if isinstance(captures['namespace_def'], list) else captures['namespace_def']
            kind = 'namespace'
            if 'name' in captures:
                name = captures['name'][0].text.decode('utf8')
            parent_name = None

        if node and name:
            docstring = _extract_docstring_from_node(node, content_bytes, lang_key)
            if parent_name and kind == 'function': kind = 'method'
            if parent_name and kind == 'async_function': kind = 'async_method'
            if parent_name and name == parent_name and kind in ('function', 'method', 'async_function', 'async_method'):
                name = f"{name}()"

            is_core = docstring and "@CORE" in docstring
            visibility = visibility_text if visibility_text else lang_def.get_visibility(captures, content_bytes)

            # Extract tags from docstring
            tags = extract_tags_from_text(docstring, node.start_point[0] + 1) if docstring else []

            sym = Symbol(
                name=name, kind=kind, line=node.start_point[0] + 1,
                docstring=docstring, signature=signature if signature else None,
                parent=parent_name, is_core=is_core, visibility=visibility,
                lang=lang_key, decorators=decorators, bases=bases,
                full_content=truncate(node.text.decode('utf8'), MAX_CONTENT_LENGTH),
                path=str(file_path) if file_path else None,
                tags=tags
            )

            node_id = node.start_byte
            if node_id in unique_symbols:
                existing = unique_symbols[node_id]
                if existing.kind in ('function', 'method') and sym.kind in ('property', 'classmethod', 'staticmethod'):
                    unique_symbols[node_id] = sym
                elif sym.signature and not existing.signature:
                    unique_symbols[node_id] = sym
            else:
                unique_symbols[node_id] = sym

    return sorted(unique_symbols.values(), key=lambda s: s.line)

def detect_languages(root_path: Path, ignore_patterns: Set[str] = None) -> Dict[str, float]:
    stats = Counter()
    total_files = 0
    ignores = list(ignore_patterns or DEFAULT_IGNORE_PATTERNS)

    for path in fs.walk_files(root_path, ignore_patterns=ignores):
        ext = path.suffix.lower()
        if ext in LANGUAGE_EXTENSIONS:
            lang = LANGUAGE_EXTENSIONS[ext]
            stats[lang] += 1
            total_files += 1

    if total_files == 0:
        return {}

    return {lang: round((count / total_files) * 100, 1) for lang, count in stats.most_common()}

def parse_requirements_txt(file_path: Path) -> List[str]:
    deps = []
    if not file_path.exists():
        return deps
    try:
        content = file_path.read_text(encoding='utf-8')
        for line in content.splitlines():
            line = line.strip()
            if not line or line.startswith('#'):
                continue
            if line.startswith(('git+', 'http', 'svn+')):
                deps.append(line)
                continue
            match = re.match(r'^([a-zA-Z0-9_\-\.]+)', line)
            if match:
                deps.append(line)
    except Exception:
        pass
    return deps

def parse_pyproject_toml(file_path: Path) -> List[str]:
    deps = []
    if not file_path.exists():
        return deps
    try:
        content = file_path.read_text(encoding='utf-8')
        in_dep_section = False
        for line in content.splitlines():
            line = line.strip()
            if line.startswith('[') and line.endswith(']'):
                section = line[1:-1]
                in_dep_section = section in ['tool.poetry.dependencies', 'project.dependencies']
                continue
            if in_dep_section and '=' in line:
                key = line.split('=')[0].strip()
                if key and key != 'python':
                    deps.append(key)
    except Exception:
        pass
    return deps

def parse_package_json(file_path: Path) -> List[str]:
    deps = []
    if not file_path.exists():
        return deps
    try:
        data = json.loads(file_path.read_text(encoding='utf-8'))
        dependencies = data.get('dependencies', {})
        dev_dependencies = data.get('devDependencies', {})
        for name, ver in dependencies.items():
            deps.append(f"{name} ({ver})")
        for name, ver in dev_dependencies.items():
            deps.append(f"{name} (dev: {ver})")
    except Exception:
        pass
    return deps

def parse_pubspec_yaml(file_path: Path) -> List[str]:
    deps = []
    if not file_path.exists():
        return deps
    try:
        content = file_path.read_text(encoding='utf-8')
        lines = content.splitlines()
        current_section = None
        for line in lines:
            stripped = line.strip()
            if not stripped or stripped.startswith('#'):
                continue
            indent = len(line) - len(line.lstrip())
            if stripped == 'dependencies:':
                current_section = 'deps'
            elif stripped == 'dev_dependencies:':
                current_section = 'dev_deps'
            elif stripped.endswith(':') and indent == 0:
                current_section = None
            elif current_section and indent > 0:
                if ':' in stripped:
                    name = stripped.split(':')[0].strip()
                    deps.append(name if current_section == 'deps' else f"{name} (dev)")
    except Exception:
        pass
    return deps

def parse_cmake_lists(file_path: Path) -> List[str]:
    deps = []
    if not file_path.exists():
        return deps
    try:
        content = file_path.read_text(encoding='utf-8')
        find_pkg_matches = re.findall(r'find_package\s*\(\s*([A-Za-z0-9_]+)', content, re.IGNORECASE)
        for name in find_pkg_matches:
            deps.append(f"find_package: {name}")
        fetch_matches = re.findall(r'FetchContent_Declare\s*\(\s*([A-Za-z0-9_]+)', content, re.IGNORECASE)
        for name in fetch_matches:
            deps.append(f"FetchContent: {name}")
    except Exception:
        pass
    return deps

def parse_csproj(file_path: Path) -> List[str]:
    deps = set()
    try:
        content = file_path.read_text(encoding='utf-8', errors='ignore')
        pattern = re.compile(r'<PackageReference\s+Include="([^"]+)"', re.MULTILINE)
        for match in pattern.finditer(content):
            deps.add(match.group(1))
    except Exception:
        pass
    return list(deps)

def extract_imports(content: str) -> List[str]:
    imports = set()
    try:
        tree = ast.parse(content)
        for node in ast.walk(tree):
            if isinstance(node, ast.Import):
                for alias in node.names:
                    imports.add(alias.name)
            elif isinstance(node, ast.ImportFrom):
                if node.module:
                    imports.add(node.module)
    except SyntaxError:
        for match in re.finditer(r"^\s*import\s+([\w.,\s]+)", content, re.MULTILINE):
            for part in match.group(1).split(','):
                imports.add(part.strip())
        for match in re.finditer(r"^\s*from\s+([\w.]+)\s+import\s+", content, re.MULTILINE):
            imports.add(match.group(1))
    except Exception:
        pass
    return list(imports)

def extract_cpp_includes(content: str) -> List[str]:
    includes = set()
    try:
        matches = re.findall(r'#\s*include\s*[<"]([^>"]+)[>"]', content)
        for inc in matches:
            includes.add(inc)
    except Exception:
        pass
    return sorted(list(includes))

def extract_dart_imports(content: str) -> List[str]:
    imports = set()
    pattern = re.compile(r"^\s*import\s+['\"](.*?)['\"]", re.MULTILINE)
    for match in pattern.finditer(content):
        imports.add(match.group(1))
    return list(imports)

def extract_csharp_usings(content: str) -> List[str]:
    usings = set()
    pattern = re.compile(r"^\s*using\s+([\w\.]+)\s*;", re.MULTILINE)
    for match in pattern.finditer(content):
        usings.add(match.group(1))
    return list(usings)

def extract_dependencies(content: str, file_path: Path) -> List[str]:
    ext = file_path.suffix.lower()
    if ext in SOURCE_PARSERS:
        try:
            return SOURCE_PARSERS[ext](content)
        except Exception:
            return []
    return []

def get_project_dependencies(root_path: Path, ignore_patterns: Set[str] = None) -> Dict[str, List[str]]:
    results = {}
    manifest_parsers = {
        'requirements.txt': parse_requirements_txt,
        'pyproject.toml': parse_pyproject_toml,
        'package.json': parse_package_json,
        'pubspec.yaml': parse_pubspec_yaml,
        'CMakeLists.txt': parse_cmake_lists,
    }
    ignores = list(ignore_patterns or DEFAULT_IGNORE_PATTERNS)

    for path in fs.walk_files(root_path, ignore_patterns=ignores):
        if path.name in manifest_parsers:
            parser = manifest_parsers[path.name]
            try:
                deps = parser(path)
                if deps:
                    results[fs.get_relative_path(path, root_path)] = deps
            except Exception: pass
        elif path.suffix.lower() == '.csproj':
             try:
                deps = parse_csproj(path)
                if deps:
                    results[fs.get_relative_path(path, root_path)] = deps
             except Exception: pass

        if path.suffix.lower() in SOURCE_PARSERS:
            parser = SOURCE_PARSERS[path.suffix.lower()]
            try:
                content = path.read_text(encoding='utf-8', errors='ignore')
                deps = parser(content)
                if deps:
                    rel_path = fs.get_relative_path(path, root_path)
                    if rel_path in results:
                        results[rel_path].extend(deps)
                    else:
                        results[rel_path] = deps
            except Exception: pass
    return results

def clean_docstring(raw: str) -> str:
    """
    Clean raw comment text by removing comment markers and trimming.
    """
    if not raw:
        return ""

    # Handle block comments
    if raw.startswith('/**'):
        lines = raw[3:-2].split('\n')
        cleaned = [line.strip().lstrip('*').strip() for line in lines]
        return "\n".join(cleaned).strip()
    elif raw.startswith('/*'):
        return raw[2:-2].strip()

    # Handle line comments
    lines = []
    for line in raw.split('\n'):
        line = line.strip()
        if line.startswith('///'):
            lines.append(line[3:].strip())
        elif line.startswith('//'):
            lines.append(line[2:].strip())
        elif line.startswith('#'):
            lines.append(line[1:].strip())
        else:
            lines.append(line)
    return "\n".join(lines).strip()

